void TrafficLight::finalize_Top( bool cancelFlag )
{
  if ( cancelFlag )
  {
    cancelAllDoActionsInside_Top_Region( );
  }

  if ( isIn_Top_Region( ) )
  {
    if ( isIn_Red_State( ) )
    {
      if ( isIn_Red_Off_State( ) )
      {
        if ( isIn_Red_Off_Flashing_State( ) )
        {
          exit_Red_Off_Flashing( cancelFlag );
        }
      }
      else if ( isIn_Red_On_State( ) )
      {
        if ( isIn_Red_On_Flashing_State( ) )
        {
          exit_Red_On_Flashing( cancelFlag );
        }
      }
      else
      {
        // blank : to satistify MISRA rules
      }
    }
    runningState.Top = Top_States::E_final;
  }
}

