pthread_t TrafficLight::launchDoAction( void *(*threadStart)(void *), TrafficLight* pStateMachine, void *stackAddr, size_t stackSize )
{
  pthread_t thr = 0;

  if ( threadStart != NULL )
  {
    pthread_attr_t attr;

    int err = pthread_attr_init( &attr );
    if ( err == 0 )
    {
      err = pthread_attr_setdetachstate( &attr, PTHREAD_CREATE_DETACHED );

      if ( err == 0 )
      {
        err = pthread_attr_setinheritsched( &attr, PTHREAD_EXPLICIT_SCHED );
      }

      if ( ( err == 0 ) && ( stackAddr != nullptr ) && ( stackSize > 0 ) )
      {
        err = pthread_attr_setstack( &attr, stackAddr, stackSize );
        err = 0;
      }

      if ( err == 0 )
      {
        err = pthread_create( &thr, &attr, threadStart, pStateMachine);
      }

      if ( err != 0 )
      {
        thr = 0;
      }

      pthread_attr_destroy( &attr );
    }
  }

  return thr;
}

void TrafficLight::killDoAction( std::size_t order )
{
  if ( doActionHandler[ order ].has_value( )
    && ! pthread_equal( doActionHandler[ order ].value( ), pthread_self( ) ) )
  {
    pthread_cancel( doActionHandler[ order ].value( ) );
    doActionHandler[ order ].reset( );
  }
}

